mapper.select:
select brand_id, `name`, logo, `description`, show_status, first_letter, sort, create_time, update_time, deleted from leyoumall.pms_brand where deleted = 0
and brand_id = #{brandId}
and `name` like concat('%', #{name}, '%')
and show_status = #{showStatus}
and first_letter = #{firstLetter}
